using UnityEngine;
using System.Collections;
using System.Runtime.InteropServices;

public static class CallIOS
{
    //当前购买的商品id
    public static string curBuyProductId = "";

    [DllImport("__Internal")]
    private static extern void _showSelectTitleDialog(string popupType, string title, string msg, string button1, string button2);
    //CallIOS.ShowSelectTitleDialog("registervisible", "提示", "是否隐藏注册界面？", "取消", "确定");
    public static void ShowSelectTitleDialog(string popupType, string title, string msg, string button1="取消", string button2="确定")
    {
        if (Application.platform == RuntimePlatform.IPhonePlayer)
        {
            _showSelectTitleDialog(popupType, title, msg, button1, button2);
        }
        else
        {
            Debug.Log("弹窗系统弹窗，popupType:"+ popupType + "    title:"+ title + "    msg:" + msg + "    button1:" + button1 + "    button2:" + button2);
            
            if (popupType == "unlogin")
            {
                CallUnity.OnSelectTitleDialogCallBack2("unlogin,1");
            }
            else if (popupType == "getNotificationPermission")
            {
                //CallUnity.OnSelectTitleDialogCallBack2("getNotificationPermission,1");
            }
        }
    }

    [DllImport("__Internal")]
    private static extern string _getDeviceIDInKeychain();

    public static string getDeviceIDInKeychain()
    {
        if (Application.platform == RuntimePlatform.IPhonePlayer)
        {
            return _getDeviceIDInKeychain();
        }
        return "testuser_100000";
    }

