    [DllImport("__Internal")]
    private static extern void _buyProduct(string productid);
    //CallIOS.BuyProduct("com.jianshenglin.tomato.gold.1");
    public static void BuyProduct(string productid)
    {
        if (curBuyProductId != "")
        {
            Debug.Log("正在充值中请稍等。。。");
            return;
        }
        curBuyProductId = productid;
        if (Application.platform == RuntimePlatform.IPhonePlayer)
        {
            _buyProduct(productid);
        }
        else
        {
            //CallUnity.OnBuyProductCallBack2("receipt" + Random.Range(1, 30000000));
            CallUnity.OnBuyProductCallBack2("");
            Debug.Log("打开充值界面！");
        }
    }

}
